import java.util.Arrays;public class Main{   public static void main(String[] args)    {      int temp;      int[] arrInts = {0, 1, 1, 0, 1, 2, 1, 2, 0, 0, 0, 1};      System.out.println("Before sort: ");      System.out.println(Arrays.toString(arrInts));      for(int a = 0; a <= arrInts.length - 1; a++)      {          for(int b = a + 1; b < arrInts.length; b++)         {            if(arrInts[a] > arrInts[b])            {               temp = arrInts[a];               arrInts[a] = arrInts[b];               arrInts[b] = temp;            }         }      }      System.out.println("After sort: ");      System.out.println(Arrays.toString(arrInts));   }}